import React, { useState } from "react";
import RegistrationOverlay1 from "./RegisterOverlay1";
//import RegistrationOverlay2 from './RegisterOverlay2'

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(email);
  };


  // Create state to track whether overlay[Modal] is open or not
  const [isOverlayOpen, setIsOverlayOpen] = useState(false);
  // Arrow function to open the overlay
  const openOverlay = () => {
    console.log("openOverlay");
    setIsOverlayOpen(true);
  };
  // Arrow function to close the overlay
  const closeOverlay = () => {
    console.log("closeOverlay");
    setIsOverlayOpen(false);
  };

  return (
    <div className="login-div">
      <div className="create-btn-container">
        <button
          className="create-button"
          data-testid="create-account-button"
          type="Create Account"
          onClick={openOverlay}
        >
          Create Account
        </button>
      </div>
      <div className="show-overlay-container">
        {/* 
            // Boolean expression to check if overlay is open or not
        */}
        {isOverlayOpen && (
          <RegistrationOverlay1 onClose={closeOverlay} onSelectMethod />
        )}
      </div>
      <h2> Login </h2>
      <div className="login-screen-container">
        <div className="login-email-container">
          <div className="form-container">
            <form className="login-form"
            data-testid="login-form" 
            onSubmit={handleSubmit}>
              <label htmlFor="email"> E-mail </label>
              <input
                type="text"
                id="email"
                data-testid="email"
                name="email"
                placeholder="E-mail"
                required
                // onChange={(event) => {
                //   setEmail(event.target.value);
                // }}
              />
              <label htmlFor="password"> Password </label>
              <input
                type="text"
                id="password"
                data-testid="password"
                name="password"
                placeholder="Password"
                required
                // onChange={(event) => {
                //   setPassword(event.target.value);
                // }}
              />
              <button className="login-button" type="submit">
                {" "}
                Login{" "}
              </button>
              <button className="link-button" type="Forgot Password">
                {" "}
                Forgot Password{" "}
              </button>
            </form>
          </div>
        </div>
        <div className="button-container">
          <button className = "google-btn" type="Sign in with Google"> Sign in with Google </button>
          <button className = "facebook-btn" type="Sign in with Facebook"> Sign in with Facebook </button>
          <button className = "apple-btn" type="Sign in with Apple"> Sign in with Apple </button>
        </div>
      </div>

      <div>
        {email}
        {password}
      </div>
    </div>
  );
};

export default Login;
